    <!-- Progress Indicator -->
    <div v-if="showProgress" class="wizard-progress">
      <div class="progress-bar">
        <div class="progress-fill" :style="{ width: `${progress}%` }" />
      </div>
      <div class="progress-text">
        Step {{ currentStepIndex + 1 }} of {{ steps.length }}
      </div>
    </div>

    <!-- Steps Navigation -->
    <div class="wizard-steps">
      <div
        v-for="(step, index) in steps"
        :key="step.id"
        class="step-item"
        :class="{
          'is-active': index === currentStepIndex,
          'is-completed': completedSteps.has(index),
          'is-clickable': allowStepNavigation
        }"
        @click="handleStepClick(index)"
      >
        <div class="step-indicator">
          <span v-if="completedSteps.has(index)" class="check-icon">‚úì</span>
          <span v-else>{{ index + 1 }}</span>
        </div>
        <div class="step-title">{{ step.title }}</div>
      </div>
    </div>

    <!-- Current Step Content -->
    <div class="wizard-content">
      <component :is="currentStep.component" />
    </div>

    <!-- Navigation Buttons -->
    <div class="wizard-actions">
      <button
        v-if="!isFirstStep"
        class="btn btn-secondary"
        @click="handlePrevious"
      >
        ‚Üê Previous
      </button>

      <button
        class="btn btn-secondary"
        @click="handleCancel"
      >
        Cancel
      </button>

      <button
        class="btn btn-primary"
        @click="handleNext"
      >
        {{ isLastStep ? 'Complete' : 'Next ‚Üí' }}
      </button>
    </div>
  </div>
</template>

