// WizardWithRouter.vue - wizard —Å —Å–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏–µ–π URL
<script setup lang="ts">
import { watch, onMounted } from 'vue'
import { useRouter, useRoute } from 'vue-router'

const router = useRouter()
const route = useRoute()

const {
  currentStepIndex,
  goToStep,
  steps
} = useWizard(wizardSteps)

// –°–∏–Ω—Ö—Ä–æ–Ω–∏–∑–∞—Ü–∏—è —Ç–µ–∫—É—â–µ–≥–æ —à–∞–≥–∞ —Å URL query parameter
watch(currentStepIndex, (newIndex) => {
  const stepId = steps[newIndex].id

  router.replace({
    query: {
      ...route.query,
      step: stepId
    }
  })
})

// –í–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ —à–∞–≥–∞ –∏–∑ URL –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
onMounted(() => {
  const stepId = route.query.step as string

  if (stepId) {
    const stepIndex = steps.findIndex(s => s.id === stepId)

    if (stepIndex !== -1) {
      goToStep(stepIndex)
    }
  }
})

// –¢–∞–∫–∂–µ –º–æ–∂–Ω–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å dynamic routes
// /registration/step/personal-info
