**4. Save/Resume functionality —Å localStorage** üî•

```typescript
// composables/useWizardPersistence.ts
export function useWizardPersistence(wizardId: string) {
  const STORAGE_KEY = `wizard_${wizardId}`

  const saveProgress = (data: {
    currentStepIndex: number
    stepsData: Record<string, any>
    completedSteps: number[]
  }) => {
    try {
      const serialized = JSON.stringify({
        ...data,
        timestamp: Date.now()
      })
      localStorage.setItem(STORAGE_KEY, serialized)
    } catch (error) {
      console.error('Failed to save wizard progress:', error)
    }
  }

  const loadProgress = () => {
    try {
      const saved = localStorage.getItem(STORAGE_KEY)
      if (!saved) return null

      const data = JSON.parse(saved)

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –Ω–µ —É—Å—Ç–∞—Ä–µ–ª–∏ –ª–∏ –¥–∞–Ω–Ω—ã–µ (–Ω–∞–ø—Ä–∏–º–µ—Ä, —Å—Ç–∞—Ä—à–µ 24 —á–∞—Å–æ–≤)
      const MAX_AGE = 24 * 60 * 60 * 1000 // 24 hours
      if (Date.now() - data.timestamp > MAX_AGE) {
        clearProgress()
        return null
      }

      return data
    } catch (error) {
      console.error('Failed to load wizard progress:', error)
      return null
    }
  }

  const clearProgress = () => {
    localStorage.removeItem(STORAGE_KEY)
  }

  return {
    saveProgress,
    loadProgress,
    clearProgress
  }
}

// –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤ wizard –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–µ
<script setup lang="ts">
const WIZARD_ID = 'registration_wizard'
const { saveProgress, loadProgress, clearProgress } = useWizardPersistence(WIZARD_ID)

const {
  currentStepIndex,
  stepsData,
  completedSteps,
  goToStep,
  setStepData
} = useWizard(steps)

// –ê–≤—Ç–æ—Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è—Ö
watch(
  [currentStepIndex, stepsData, completedSteps],
  () => {
    saveProgress({
      currentStepIndex: currentStepIndex.value,
      stepsData: getAllData(),
      completedSteps: Array.from(completedSteps.value)
    })
  },
  { deep: true }
)

// –í–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏–µ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
onMounted(() => {
  const saved = loadProgress()

  if (saved) {
    // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –¥–∏–∞–ª–æ–≥ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
    const shouldResume = confirm('Would you like to resume your previous session?')

    if (shouldResume) {
      // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ
      Object.entries(saved.stepsData).forEach(([stepId, data]) => {
        setStepData(stepId, data)
      })

      saved.completedSteps.forEach((index: number) => {
        completedSteps.value.add(index)
      })

      goToStep(saved.currentStepIndex)
    } else {
      clearProgress()
    }
  }
})

// –û—á–∏—Å—Ç–∫–∞ –ø—Ä–∏ –∑–∞–≤–µ—Ä—à–µ–Ω–∏–∏
const handleComplete = () => {
  clearProgress()
  emit('complete', getAllData())
}
</script>
```

