import { describe, it, expect, vi, beforeEach } from 'vitest'
import { mount } from '@vue/test-utils'
import { createRouter, createMemoryHistory } from 'vue-router'
import type { Router } from 'vue-router'

// ‚ùå –ü–õ–û–•–û: –°–ª–∏—à–∫–æ–º –≥—Ä—É–±–æ–µ –º–æ–∫–∏—Ä–æ–≤–∞–Ω–∏–µ
vi.mock('vue-router', () => ({
  useRouter: vi.fn(),
  useRoute: vi.fn()
}))

// ‚úÖ –•–û–†–û–®–û: –°–æ–∑–¥–∞–Ω–∏–µ —Ä–µ–∞–ª—å–Ω–æ–≥–æ router –¥–ª—è —Ç–µ—Å—Ç–æ–≤
const createMockRouter = (options: { currentRoute?: any } = {}): Router => {
  const router = createRouter({
    history: createMemoryHistory(),
    routes: [
      { path: '/', component: { template: '<div>Home</div>' } },
      { path: '/user/:id', component: { template: '<div>User</div>' } }
    ]
  })

  if (options.currentRoute) {
    router.push(options.currentRoute)
  }

  return router
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç —Å —Ä–æ—É—Ç–∏–Ω–≥–æ–º
