    const goBack = () => {
      router.push('/')
    }

    const goToUser = (id: number) => {
      router.push(`/user/${id}`)
    }

    return {
      userId,
      goBack,
      goToUser
    }
  }
})

describe('UserProfile - Router Mocking', () => {
