// Error Boundary –∫–æ–º–ø–æ–Ω–µ–Ω—Ç
interface ErrorBoundaryProps {
  fallback?: (error: Error) => VNode
  onError?: (error: Error) => void
}

const ErrorBoundary = defineComponent({
  name: 'ErrorBoundary',
  props: {
    fallback: Function as PropType<(error: Error) => VNode>,
    onError: Function as PropType<(error: Error) => void>
  },
  setup(props, { slots }) {
    const error = ref<Error | null>(null)

    const errorCaptured = (err: Error): boolean => {
      error.value = err
      props.onError?.(err)
      return false // –û—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –≤—Å–ø–ª—ã—Ç–∏–µ
    }

    onErrorCaptured(errorCaptured)

    return () => {
      if (error.value) {
        return props.fallback
          ? props.fallback(error.value)
          : h('div', { class: 'error' }, `Error: ${error.value.message}`)
      }
      return slots.default?.()
    }
  }
})

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∫–æ—Ç–æ—Ä—ã–π –º–æ–∂–µ—Ç –≤—ã–±—Ä–æ—Å–∏—Ç—å –æ—à–∏–±–∫—É
