```typescript
interface DataState<T> {
  data: T | null
  loading: boolean
  error: Error | null
}

// Composable –¥–ª—è —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è async state
const useAsyncData = <T>(fetcher: () => Promise<T>) => {
  const state = reactive<DataState<T>>({
    data: null,
    loading: false,
    error: null
  })

  const execute = async (): Promise<void> => {
    state.loading = true
    state.error = null

    try {
      state.data = await fetcher()
    } catch (err) {
      state.error = err as Error
    } finally {
      state.loading = false
    }
  }

  const reset = (): void => {
    state.data = null
    state.loading = false
    state.error = null
  }

  return {
    ...toRefs(state),
    execute,
    reset
  }
}

const DataComponent = defineComponent({
  name: 'DataComponent',
  setup() {
    const { data, loading, error, execute, reset } = useAsyncData(async () => {
      const response = await fetch('/api/data')
      if (!response.ok) throw new Error('Failed to fetch')
      return response.json()
    })

    onMounted(() => {
      execute()
    })

    return {
      data,
      loading,
      error,
      execute,
      reset
    }
  },
  template: `
    <div>
      <div v-if="loading" class="loading">Loading...</div>
      <div v-else-if="error" class="error">{{ error.message }}</div>
      <div v-else-if="data" class="data">{{ data }}</div>
      <button @click="execute">Retry</button>
      <button @click="reset">Reset</button>
    </div>
  `
})

describe('Loading and Error States', () => {
  // ‚úÖ –¢–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ loading state
  it('–ø–æ–∫–∞–∑—ã–≤–∞–µ—Ç loading –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä', async () => {
    let resolvePromise: (value: any) => void
    global.fetch = vi.fn().mockReturnValue({
      ok: true,
      json: () => new Promise(resolve => {
        resolvePromise = resolve
      })
    })

    const wrapper = mount(DataComponent)

    // –°—Ä–∞–∑—É –ø–æ—Å–ª–µ –º–æ–Ω—Ç–∏—Ä–æ–≤–∞–Ω–∏—è - loading
    await nextTick()
    expect(wrapper.vm.loading).toBe(true)
    expect(wrapper.find('.loading').exists()).toBe(true)

    // –†–µ–∑–æ–ª–≤–∏–º –¥–∞–Ω–Ω—ã–µ
    resolvePromise!({ message: 'Success' })
    await flushPromises()

    expect(wrapper.vm.loading).toBe(false)
    expect(wrapper.find('.loading').exists()).toBe(false)
    expect(wrapper.find('.data').exists()).toBe(true)
  })

  // üî• –¢–µ—Å—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ error state
  it('–ø–æ–∫–∞–∑—ã–≤–∞–µ—Ç –æ—à–∏–±–∫—É', async () => {
    global.fetch = vi.fn().mockResolvedValue({
      ok: false
    })

