```typescript
// Test helpers –¥–ª—è async mocking
class AsyncMock<T> {
  private resolvers: Array<(value: T) => void> = []
  private rejecters: Array<(error: Error) => void> = []
  private results: Array<T | Error> = []
  private currentIndex = 0

  constructor(private mockFn: any) {}

  mockResolvedValueOnce(value: T): this {
    this.results.push(value)
    return this
  }

  mockRejectedValueOnce(error: Error): this {
    this.results.push(error)
    return this
  }

  async next(): Promise<T> {
    const result = this.results[this.currentIndex++]

    if (result instanceof Error) {
      throw result
    }

    return result as T
  }

  getCallCount(): number {
    return this.currentIndex
  }
}

// Utility –¥–ª—è –∫–æ–Ω—Ç—Ä–æ–ª–∏—Ä—É–µ–º—ã—Ö –ø—Ä–æ–º–∏—Å–æ–≤
class DeferredPromise<T> {
  promise: Promise<T>
  resolve!: (value: T) => void
  reject!: (error: Error) => void

  constructor() {
    this.promise = new Promise((resolve, reject) => {
      this.resolve = resolve
      this.reject = reject
    })
  }
}

describe('Advanced Async Mocking', () => {
  it('–∏—Å–ø–æ–ª—å–∑—É–µ—Ç DeferredPromise –¥–ª—è –∫–æ–Ω—Ç—Ä–æ–ª—è', async () => {
    const deferred = new DeferredPromise<any>()

    global.fetch = vi.fn().mockReturnValue({
      ok: true,
      json: () => deferred.promise
    })

    const wrapper = mount(DataComponent)

    await nextTick()
    expect(wrapper.vm.loading).toBe(true)

    // –ö–æ–Ω—Ç—Ä–æ–ª–∏—Ä—É–µ–º –∫–æ–≥–¥–∞ —Ä–µ–∑–æ–ª–≤–∏—Ç—å
    deferred.resolve({ message: 'Controlled' })
    await flushPromises()

    expect(wrapper.vm.loading).toBe(false)
    expect(wrapper.vm.data).toEqual({ message: 'Controlled' })
  })

  it('—Å–∏–º—É–ª–∏—Ä—É–µ—Ç –º–µ–¥–ª–µ–Ω–Ω—É—é —Å–µ—Ç—å', async () => {
    const delay = (ms: number) => new Promise(resolve => setTimeout(resolve, ms))

    global.fetch = vi.fn().mockImplementation(async () => {
      await delay(100)
      return {
        ok: true,
        json: async () => ({ message: 'Slow response' })
      }
    })

    const wrapper = mount(DataComponent)

    await nextTick()
    expect(wrapper.vm.loading).toBe(true)

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —á—Ç–æ loading –ø—Ä–æ–¥–æ–ª–∂–∞–µ—Ç—Å—è
    await delay(50)
    expect(wrapper.vm.loading).toBe(true)

    // –ñ–¥–µ–º –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è
    await flushPromises()
    await delay(100)

    expect(wrapper.vm.loading).toBe(false)
  })
})
