const Counter = defineComponent({
  name: 'Counter',
  setup() {
    const count = ref(0)

    const increment = () => {
      count.value++
    }

    return {
      count,
      increment
    }
  },
  template: `
    <div>
      <p>Count: {{ count }}</p>
      <button @click="increment">Increment</button>
    </div>
  `
})

// –¢–µ—Å—Ç –ø—Ä–æ—Ö–æ–¥–∏—Ç! ‚úÖ

// STEP 3: REFACTOR - —É–ª—É—á—à–∞–µ–º –∫–æ–¥
const Counter = defineComponent({
  name: 'Counter',
  props: {
    initialCount: {
      type: Number,
      default: 0
    },
    step: {
      type: Number,
      default: 1
    }
  },
  setup(props) {
    const count = ref(props.initialCount)

    const increment = () => {
      count.value += props.step
    }

    const decrement = () => {
      count.value -= props.step
    }

    return {
      count,
      increment,
      decrement
    }
  }
})

// –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–≤—ã–µ —Ç–µ—Å—Ç—ã
